p {
  margin-top: 0; }

/* Typography
 */
h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: "Montserrat", sans-serif;
  font-weight: 700;
  margin-bottom: 2rem;
  margin-top: 0; }

h1 {
  font-size: 4.0rem;
  letter-spacing: -0.1rem;
  line-height: 1.2; }

h2 {
  font-size: 3.6rem;
  letter-spacing: -0.1rem;
  line-height: 1.25; }

h3 {
  font-size: 3.0rem;
  letter-spacing: -0.1rem;
  line-height: 1.3; }

h4 {
