  h1 {
    font-size: 5.0rem; }

  h2 {
    font-size: 4.2rem; }

  h3 {
    font-size: 3.6rem; }

  h4 {
    font-size: 3.0rem; }

  h5 {
    font-size: 2.4rem; }

  h6 {
    font-size: 1.5rem; } }
/* Forms
 */
form {
  margin-bottom: 2.5rem; }

input,
textarea,
select,
fieldset {
  margin-bottom: 1.5rem; }

input[type="date"],
input[type="email"],
input[type="number"],
input[type="search"],
input[type="text"],
input[type="tel"],
input[type="url"],
input[type="password"],
textarea,
select {
  background-color: #fff;
  border: 1px solid #bdc3c7;
  border-radius: 4px;
  box-shadow: none;
  height: 38px;
  padding: 6px 10px; }

/* Removes awkard default styles on some inputs for iOS */
input[type="date"],
input[type="email"],
input[type="number"],
input[type="search"],
input[type="text"],
input[type="tel"],
input[type="url"],
input[type="password"],
textarea {
  -webkit-appearance: none;
  -moz-appearance: none;
  -ms-appearance: none;
  -o-appearance: none;
  appearance: none; }

textarea {
  min-height: 65px;
  padding-bottom: 6px;
  padding-top: 6px; }

input[type="date"]:focus,
input[type="email"]:focus,
input[type="number"]:focus,
input[type="search"]:focus,
input[type="text"]:focus,
input[type="tel"]:focus,
input[type="url"]:focus,
input[type="password"]:focus,
textarea:focus,
select:focus {
  border: 1px solid #3498db;
  outline: 0; }

label,
legend {
  display: block;
  font-weight: 700;
  margin-bottom: 0.5rem; }

fieldset {
  border-width: 0;
  padding: 0; }

input[type="checkbox"],
input[type="radio"] {
  display: inline; }

label > .label--body {
  display: inline-block;
  font-weight: normal;
  margin-left: 0.5rem; }

/* Grid
 */
.container {
  margin: 0 auto;
  max-width: 960px;
  padding: 0 20px;
  position: relative;
  width: 100%; }

.column,
.columns {
  float: left;
  width: 100%; }

/* For devices larger than mobile */
@media (min-width: 400px) {
  .container {
    padding: 0;
    width: 85%; } }
/* For devices larger than phablet */
@media (min-width: 550px) {
  .container {
    width: 80%; }

  .column,
  .columns {
    margin-left: 4.0%; }
    .column:first-child,
    .columns:first-child {
      margin-left: 0; }

  /* Grid Classes */
  /* Singular */
  .column.one {
    width: 4.66667%; }
  .column.one-third {
    width: 30.66667%; }
  .column.two-thirds {
    width: 65.33333%; }
  .column.one-half {
    width: 48.0%; }

  /* Plural */
  .columns.one {
    width: 4.66667%; }
  .columns.two {
    width: 13.33333%; }
  .columns.three {
    width: 22.0%; }
  .columns.four {
    width: 30.66667%; }
  .columns.five {
    width: 39.33333%; }
  .columns.six {
    width: 48.0%; }
  .columns.seven {
    width: 56.66667%; }
  .columns.eight {
    width: 65.33333%; }
  .columns.nine {
    width: 74.0%; }
  .columns.ten {
    width: 82.66667%; }
  .columns.eleven {
    width: 91.33333%; }
  .columns.twelve {
    width: 100%;
    margin-left: 0; }

  /* Offsets */
  .column.offset-by-one,
  .columns.offset-by-one {
    margin-left: 8.66667%; }
  .column.offset-by-two,
  .columns.offset-by-two {
    margin-left: 17.33333%; }
  .column.offset-by-three,
  .columns.offset-by-three {
    margin-left: 26.0%; }
  .column.offset-by-four,
  .columns.offset-by-four {
    margin-left: 34.66667%; }
  .column.offset-by-five,
  .columns.offset-by-five {
    margin-left: 43.33333%; }
  .column.offset-by-six,
  .columns.offset-by-six {
    margin-left: 52.0%; }
  .column.offset-by-seven,
  .columns.offset-by-seven {
    margin-left: 60.66667%; }
  .column.offset-by-eight,
  .columns.offset-by-eight {
    margin-left: 69.33333%; }
  .column.offset-by-nine,
  .columns.offset-by-nine {
    margin-left: 78.0%; }
  .column.offset-by-ten,
  .columns.offset-by-ten {
    margin-left: 86.66667%; }
  .column.offset-by-eleven,
  .columns.offset-by-eleven {
    margin-left: 95.33333%; }
  .column.offset-by-one-third,
  .columns.offset-by-one-third {
    margin-left: 34.66667%; }
  .column.offset-by-two-thirds,
  .columns.offset-by-two-thirds {
    margin-left: 69.33333%; }
  .column.offset-by-one-half,
  .columns.offset-by-one-half {
    margin-left: 52.0%; } }
