  white-space: nowrap;
  border-radius: 4px;
  cursor: pointer;
  margin-bottom: 1rem; }
  button:hover, button:focus {
    outline: 0; }

/* Button Generator */
/* Basic button */
button,
.btn {
  font-family: "Montserrat", sans-serif;
  font-weight: 400;
  display: inline-block;
  height: 38px;
  padding: 0 30px;
  text-align: center;
  font-size: 11px;
  line-height: 38px;
  letter-spacing: 0.1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  border-radius: 4px;
  cursor: pointer;
  background-color: transparent;
  color: #95a5a6;
  border: 1px solid #95a5a6; }
  button:hover, button:focus,
  .btn:hover,
  .btn:focus {
    outline: 0; }
  button:hover, button:focus,
  .btn:hover,
  .btn:focus {
    background-color: transparent;
    border-color: #617374;
    color: #617374; }

/* Primary Button */
.btn--primary {
  font-family: "Montserrat", sans-serif;
  font-weight: 400;
  display: inline-block;
  height: 38px;
  padding: 0 30px;
  text-align: center;
  font-size: 11px;
  line-height: 38px;
  letter-spacing: 0.1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  border-radius: 4px;
  cursor: pointer;
  background-color: #3498db;
  color: #fff;
  border: 1px solid #3498db; }
  .btn--primary:hover, .btn--primary:focus {
    outline: 0; }
  .btn--primary:hover, .btn--primary:focus {
    background-color: #258cd1;
    border-color: #258cd1;
    color: #fff; }

/* Success Button */
.btn--success {
  font-family: "Montserrat", sans-serif;
  font-weight: 400;
  display: inline-block;
  height: 38px;
  padding: 0 30px;
  text-align: center;
  font-size: 11px;
  line-height: 38px;
  letter-spacing: 0.1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  border-radius: 4px;
  cursor: pointer;
  background-color: #2ecc71;
  color: #fff;
  border: 1px solid #2ecc71; }
  .btn--success:hover, .btn--success:focus {
    outline: 0; }
  .btn--success:hover, .btn--success:focus {
    background-color: #29b765;
    border-color: #29b765;
    color: #fff; }

/* Danger Button */
.btn--danger {
  font-family: "Montserrat", sans-serif;
  font-weight: 400;
  display: inline-block;
  height: 38px;
  padding: 0 30px;
  text-align: center;
  font-size: 11px;
  line-height: 38px;
  letter-spacing: 0.1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  border-radius: 4px;
  cursor: pointer;
  background-color: #e74c3c;
  color: #fff;
  border: 1px solid #e74c3c; }
  .btn--danger:hover, .btn--danger:focus {
    outline: 0; }
  .btn--danger:hover, .btn--danger:focus {
    background-color: #e43725;
    border-color: #e43725;
    color: #fff; }

/* Utilities
 */
.u-full-width {
